	public City citySearch(String name) {

		int tracker = 0;


		while (name.toLowerCase().compareTo(cities.get(tracker).getName().toLowerCase()) != 0) {
			tracker++;
		}
		return cities.get(tracker);
	}

	public ArrayList<Road> roadSearch(City a, City b) {
		ArrayList<Road> result = new ArrayList<>();
		for (Road n: a.getRoads()) {
			if (n.getOtherNode(a).getName().equals(b.getName())) {
				result.add(n);
			}
		}

		return result;
	}

	public ArrayList<City> getAvailableAdjacentCities(City a) {
		ArrayList<City> result = new ArrayList<>();
		for (Road n: a.getRoads()) {
			if (!n.isTaken()) {
				result.add(n.getOtherNode(a));
			}
		}
		return result;
	}



